import React, { useEffect } from "react";

import Map from "ol/Map";
import View from "ol/View";
import TileLayer from "ol/layer/Tile";
import VectorLayer from "ol/layer/Vector";
import OSM from "ol/source/OSM";
import { fromLonLat } from "ol/proj.js";
import Feature from "ol/Feature";
import Point from "ol/geom/Point";
import VectorSrc from "ol/source/Vector";
import { Icon, Style } from "ol/style";
import { getData } from "../Gallery";

const artIcon =
  "data:image/svg+xml;utf8;base64,";

const iconStyle = new Style({
  image: new Icon({
    anchor: [0.5, 46],
    anchorXUnits: "fraction",
    anchorYUnits: "pixels",
    src: artIcon,
    scale: 0.04
  })
});

let STHLMmap = "";

export function ArtMap() {
  useEffect(() => {
    setMap();
  }, []);
  return <div className="map" id="map" />;
}

function setMap() {
  const mapCenter = fromLonLat([18.068581, 59.329323]);

  STHLMmap = new Map({
    view: new View({
      center: mapCenter,
      zoom: 13
    }),
    layers: [
      new TileLayer({
        source: new OSM()
      })
    ],
    target: "map"
  });
  const data = getData();
  if (data) {
    data.map(el => addMarker(el.title, el.longitude, el.latitude));
  }
}

function addMarker(name, lon, lat) {
  const marker = new Feature({
    geometry: new Point(fromLonLat([lon, lat])),
    name: `${name}`
  });
  marker.setStyle(iconStyle);

  const src = new VectorSrc({
    features: [marker]
  });

  const markersLayer = new VectorLayer({
    source: src
  });

  return STHLMmap.addLayer(markersLayer);
}

export default addMarker;
